(254, 'wpv_custom_css', '.home .wpcf7-response-output {\r\ncolor: white;\r\n}\r\n.touch .header-content-wrapper {\r\n	background-color: #ffffff;\r\n}\r\n.touch .tp-caption img {\r\n	width: 165px!important;\r\n	height: 142px!important;\r\n}\r\n.responsive-layout #mp-menu-trigger:before {\r\n	color: #7f968e;\r\n}\r\n#blog {\r\n	display:none!important;\r\n}\r\n\r\n.zoom-instagram-widget__item:first-child {\r\n	margin-top:10px!important;\r\n}\r\n\r\n.page-wrapper > div.right-only, .page-wrapper > article.right-only, .page-wrapper > .ajax-result > div.right-only, .page-wrapper > .ajax-result > article.right-only {\r\n	width: 70%;\r\n}\r\n.page-wrapper > aside.right, .page-wrapper > .ajax-result > aside.right {\r\n	width: 30%;\r\n}\r\n#main-menu .menu > .menu-item.current-menu-parent > a, #main-menu .menu > .menu-item.current-menu-item > a, #main-menu .menu > .menu-item.current-menu-ancestor > a, #main-menu .menu > .menu-item.current-menu-ancestor > a, #main-menu .menu > .menu-item:hover > a {\r\n\r\n}\r\n.responsive-layout .mp-level {\r\n	background-color: #d1cfca;\r\n}\r\n.logo {\r\n	width: 170px;\r\n	height: 100px!important;\r\n	display: block;\r\n	content: \\"\\";\r\n	margin: 15px auto;\r\n	text-indent: -9999px;\r\n}\r\n\r\n#top-nav-wrapper,\r\n.logo img,\r\n.logo-tagline {\r\n	display: none!important;\r\n}\r\n\r\n\r\n.home.page.wpv-scrolled .logo {\r\n	background: url(\\\'wp-content/uploads/2016/06/logo-gray-fox.svg\\\') no-repeat center center;\r\n}\r\n.touch .home.page.wpv-scrolled .logo,\r\n.touch .home.page.wpv-not-scrolled .logo {\r\n	background: url(\\\'wp-content/uploads/2016/06/logo-gray-fox.svg\\\') no-repeat center center;\r\n}\r\n.page .logo {\r\n	background: url(\\\'../wp-content/uploads/2016/06/logo-gray-fox.svg\\\') no-repeat center center;\r\n}\r\n\r\n.logo-tagline {\r\n	display: none;\r\n}\r\n\r\n.social-icon {\r\n	margin: 0 5px;\r\n}\r\n\r\n.social-icon:nth-child(2) {\r\n	margin-right:15px;\r\n}\r\n\r\n#about p,\r\n#about h2 {\r\n	max-width: 800px;\r\n	margin-left: auto;\r\n	margin-right: auto;\r\n}\r\n.fox {\r\n	display:block;\r\n	margin: 0 auto;\r\n	width: auto;\r\n}\r\n\r\n.fox img {\r\n	width: 120px;\r\n	margin: 0 auto;\r\n}\r\n.block-weddings .icon,\r\n.block-flowers .icon {\r\n	width: 100px;\r\n	height: 100px;\r\n	position:absolute;\r\n	bottom: 20px;\r\n	left: 0;\r\n	right: 0;\r\n	margin: auto;\r\n	display: block;\r\n	content: \\"\\";\r\n}\r\n\r\n.block-weddings .icon {\r\n	background: url(\\\'wp-content/uploads/2016/05/icon-ring.svg\\\') no-repeat center center;\r\ntext-indent: 9999px;\r\n}\r\n\r\n.block-flowers .icon {\r\n	background: url(\\\'wp-content/uploads/2016/05/icon-flowers.svg\\\') no-repeat center center;\r\ntext-indent: 9999px;\r\n}\r\n\r\n.event-time-inner {\r\n	height: 110px!important;\r\n}\r\n\r\n.block-weddings .event-time .event-time-right {\r\n	padding-left: 50px;\r\n}\r\n.block-weddings .event-time .event-time-left {\r\n	padding-right: 50px;\r\n}\r\nsvg.flower {\r\n	display: none; \r\n} \r\n\r\n.left50 {\r\n	width: calc(50% - 15px);\r\n	float:left;\r\n}\r\n\r\n.right50 {\r\n	width: calc(50% - 15px);\r\n	float:right;\r\n}\r\n\r\ninput.wpcf7-submit {\r\n	margin: 0 auto;\r\n	background-color: #c29999!important;\r\n}\r\n\r\n.page article input,\r\n.page article textarea {\r\n	border: 1px solid #cccccc;\r\n}\r\n\r\n.page article input.wpcf7-submit {\r\n	margin: 0px;\r\n	border: 0px;\r\n}\r\n.home.page.wpv-not-scrolled .logo {\r\n	background: url(\\\'wp-content/uploads/2016/06/logo-white-fox.svg\\\') no-repeat center center;\r\n}\r\n\r\n@media (max-width: 480px) {\r\n	.right50,\r\n	.left50 {\r\n		width: 100%;\r\n	}\r\n}\r\n@media (max-width: 958px) {\r\n	.fox,\r\n	.hide {\r\n		display:none;\r\n	}\r\n	.home.page.wpv-not-scrolled .logo {\r\n		\r\n	background: url(\\\'wp-content/uploads/2016/06/logo-gray-fox.svg\\\') no-repeat center center!important;\r\n	}\r\n}', 'yes'),
