(8282, 1, '2016-07-08 02:17:04', '2016-07-08 02:17:04', '[column parallax_bg="disabled" parallax_bg_inertia="-0.2" extended="false" extended_padding="true" background_color="" background_image="" background_repeat="" background_position="" background_size="auto" background_attachment="" hide_bg_lowres="false" background_video="" vertical_padding_top="0" vertical_padding_bottom="0" more_link="" more_text="" left_border="transparent" class="" id="about" title="" title_type="single" animation="zoom-in" width="1/1" last="true"]\r\n<p style="text-align: center; -ms-transform: rotate(-15deg); -webkit-transform: rotate(-15deg); transform: rotate(-15deg);"><em>Welcome!</em></p>\r\n[push h="20"]\r\n<h2 class="headline" style="text-align: center;">There is nothing better than seeing a bride\'s face when she sees her bouquet for the first time, except when the flowers are straight from the farm!</h2>\r\n<p style="text-align: center;">Located in Honesdale, Pennsylvania, Fox Hill Farm is a small farm with a big vision. The owners, Katharine and George Brown, use their 50 acre farm to produce fresh cut seasonal flowers, raise 100% grass fed beef and pastured poultry, and provide an atmosphere for appreciating the beauty of nature through tours, workshops, and weddings!</p>\r\n\r\n<h5 style="text-align: center;">EXPLORE THE SITE TO SEE MORE ABOUT OUR WORK, OUR FARM, AND BEYOND!</h5>\r\n[push h="15"]\r\n<p style="text-align: center;">[icon name="flower" style="" color="accent3" size="26"]</p>\r\n[/column]\r\n\r\n[column parallax_bg="disabled" parallax_bg_inertia="-0.2" extended="true" extended_padding="true" background_color="" background_image="" background_repeat="" background_position="" background_size="auto" background_attachment="" hide_bg_lowres="false" background_video="" vertical_padding_top="0" vertical_padding_bottom="0" more_link="" more_text="" left_border="transparent" class="" id="services" title="OUR SERVICES" title_type="no-divider" animation="none" width="1/1" last="true"]\r\n\r\n[column_1 parallax_bg="to-centre" parallax_bg_inertia="0.1" extended="true" extended_padding="true" background_color="" background_image="http://foxhillfarm.dev/wp-content/uploads/2016/06/banner-services-02.png" background_repeat="no-repeat" background_position="" background_size="cover" background_attachment="" hide_bg_lowres="false" background_video="" vertical_padding_top="0" vertical_padding_bottom="0" more_link="" more_text="" left_border="transparent" class="" id="" title="" title_type="single" animation="none" width="1/1" last="true"]\r\n\r\n[column_2 parallax_bg="disabled" parallax_bg_inertia="-0.2" extended="false" extended_padding="true" background_color="" background_image="http://foxhillfarm.dev/wp-content/themes/honeymoon/samples/images/element_7.png" background_repeat="no-repeat" background_position="center bottom" background_size="auto" background_attachment="" hide_bg_lowres="false" background_video="" vertical_padding_top="65" vertical_padding_bottom="70" more_link="" more_text="" left_border="transparent" class="" id="" title="" title_type="single" animation="fade-in" width="1/1" last="true"]\r\n\r\n[column_3 width="1/3" title="undefined" title_type="undefined" animation="from-left" implicit="true"]\r\n\r\n[wpv_timed_event style="light" title="WEDDINGS" start_time="" end_time="" date="" button_link="http://foxhillfarm.dev/weddings" button_text="LEARN MORE →"]\r\n<p class="p1"><span class="s1">Happily ever afters start here! </span><span class="s2">Let the gardens, pastures, and barn here at Fox Hill Farm paint a wonderful backdrop for your farm wedding.</span></p>\r\n[push h="15"]\r\n\r\n[/wpv_timed_event]\r\n\r\n[/column_3]\r\n\r\n[column_3 width="1/3" title="" title_type="single" animation="none" implicit="true"]\r\n\r\n[push h="130"]\r\n<div class="fox"><img src="http://foxhillfarm.dev/wp-content/uploads/2016/05/fox.svg" alt="" /></div>\r\n[/column_3]\r\n\r\n[column_3 width="1/3" last="true" title="undefined" title_type="undefin